mapper.update:
update worksystem



        type=#{worksystem.type},


        startTime=#{worksystem.startTime},


        endTime=#{worksystem.endTime}


where id=#{worksystem.id}

mapper.delete:
delete from workorder
where id in

    #{id}

delete from worksystem
where woid in

    #{id}

delete from scheduling
where month=#{scheduling.month} and day=#{scheduling.day}
 and woid=#{scheduling.woid} and type=#{scheduling.type}

delete from scheduling
where 1=1

    and month=#{month}


    and woid=#{woid}
