mapper.insert:

insert into promoter_qr_code (pro_info_id, dest_id, shangjia_name, `map`, map_name, `floor`, x, y, fid,`desc`,create_time)
values (#{proInfoId,jdbcType=INTEGER}, #{destId,jdbcType=INTEGER}, #{shangjiaName,jdbcType=VARCHAR}, 
  #{map,jdbcType=VARCHAR}, #{mapName,jdbcType=VARCHAR}, #{floor,jdbcType=VARCHAR}, 
  #{x,jdbcType=VARCHAR}, #{y,jdbcType=VARCHAR}, #{fid,jdbcType=VARCHAR},#{desc,jdbcType=VARCHAR},#{createTime}
  )

mapper.update:

update promoter_qr_code
set qrcode = #{qrcode,jdbcType=VARCHAR},qrcodelocal =#{qrcodelocal}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select 
from promoter_qr_code pqc
left join promoter_info pi on pqc.pro_info_id = pi.id
where 1=1

  and pi.name like concat('%',#{name},'%')


  and pqc.shangjia_name like concat('%',#{shangJiaName},'%')


  and pqc.map = #{map}


  and pqc.map in

    #{mapids}



  order by ${desc}

mapper.delete:
delete from promoter_qr_code
where id in

  #{id}

mapper.select:
select id, pro_info_id, dest_id, shangjia_name, map, map_name,`desc`
from promoter_log
where pro_info_id = #{id}
